tion=\"replace\">\n <![CDATA[\n if (isset($this->request->get[\'modification_editor\'])) { \n echo $this->session->data[\'success\'];\n exit;\n } else {\n $this->response->redirect($this->url->link(\'extension/module/modification\', \'user_token=\' . $this->session->data[\'user_token\'] . $url, true));\n }\n ]]>\n </add>\n </operation>\n </file>\n\n <file path=\"admin/view/template/marketplace/modification.twig\">\n <operation>\n <search>\n <![CDATA[<div class=\"pull-right\">]]>\n </search>\n <add position=\"after\">\n <![CDATA[<a href=\"{{new}}\" data-toggle=\"tooltip\" title=\"{{button_new}}\" class=\"btn btn-primary\"><i class=\"fa fa-plus\"></i></a>]]>\n </add>\n </operation>\n <operation>\n <search>\n <![CDATA[{% if not modification.enabled %}]]>\n </search>\n <add position=\"before\">\n <![CDATA[\n <a href=\"{{modification.edit}}\" data-toggle=\"tooltip\" title=\"{{button_edit}}\" class=\"btn btn-primary\"><i class=\"fa fa-pencil\"></i></a>\n <a href=\"{{modification.download}}\" data-toggle=\"tooltip\" title=\"{{button_download}}\" class=\"btn btn-success\"><i class=\"fa fa-cloud-download\"></i></a>\n ]]>\n </add>\n </operation>\n </file>\n</modification> ","1","2017-06-22 17:05:11"),
(null,"0","So ThemeConfig","ocmod-soconfig","OpenCartWorks","3.0.0","http://www.opencartworks.com","<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So ThemeConfig]]></name> \n <code>ocmod-soconfig</code> \n <version><![CDATA[3.0.0]]></version> \n <author><![CDATA[OpenCartWorks]]></author> \n <link>http://www.opencartworks.com</link> \n<!--========Catalog/controller/common=============--> \n <file path=\"catalog/controller/common/home.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'content_home\'] = $this->load->controller(\'extension/soconfig/content_home\'); \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/{header}*.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $data[\'soconfig\'] = $this->soconfig; \n $this->load->language(\'extension/soconfig/compare\'); \n $data[\'objlang\'] = $this->language; \n $data[\'lang_id\'] = $this->config->get(\'config_language_id\'); \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'url_layoutbox\'] = isset($this->request->get[\'layoutbox\']) ? $this->request->get[\'layoutbox\'] : \'\' ; \n $data[\'url_pattern\'] = isset($this->request->get[\'pattern\']) ? $this->request->get[\'pattern\'] : \'\' ; \n $data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ; \n $data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true); \n // add position \n $data[\'content_menu\'] = $this->load->controller(\'extension/soconfig/content_menu\'); \n $data[\'content_menu1\'] = $this->load->controller(\'extension/soconfig/content_menu1\'); \n $data[\'content_menu2\'] = $this->load->controller(\'extension/soconfig/content_menu2\'); \n $data[\'header_block\'] = $this->load->controller(\'extension/soconfig/header_block\'); \n $data[\'search_block\'] = $this->load->controller(\'extension/soconfig/search_block\'); \n \n // For page specific css \n if (isset($this->request->get[\'route\'])) { \n if (isset($this->request->get[\'product_id\'])) { \n $class = \'-\' . $this->request->get[\'product_id\']; \n } elseif (isset($this->request->get[\'path\'])) { \n $class = \'-\' . $this->request->get[\'path\']; \n } elseif (isset($this->request->get[\'manufacturer_id\'])) { \n $class = \'-\' . $this->request->get[\'manufacturer_id\']; \n } elseif (isset($this->request->get[\'information_id\'])) { \n $class = \'-\' . $this->request->get[\'information_id\']; \n } else { \n $class = \'\'; \n } \n \n $data[\'class\'] = str_replace(\'/\', \'-\', $this->request->get[\'route\']) . $class; \n } else { \n $data[\'class\'] = \'common-home\'; \n } \n \n //Decodes HTML Entities \n $data[\'selector_body\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_body\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'selector_menu\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_menu\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'selector_heading\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_heading\'), ENT_QUOTES, \'UTF-8\'); \n \n ]]></add> \n </operation> \n </file> \n <file path=\"catalog/controller/common/{footer.php}\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $data[\'soconfig\'] = $this->soconfig; \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ; \n $data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true); \n \n if( $this->soconfig->get_settings(\'typefooter\') == 1) $data[\'footer_1\'] = $this->load->controller(\'extension